package com.dns.taxchina.service.db.fileDB;import netlib.util.AppUtil;import android.content.Context;import android.content.SharedPreferences;import android.content.SharedPreferences.Editor;/** * @author henzil * @version create time:2014-6-4_下午10:48:46 * @Description  用来判断是否是此版本的version  */public class DBVersionSharedPreferences {	private static final String DB_SHARED_PREFERENCES = "db_SharedPreferences";	private static final String VERSION = "version";		public static void saveVersionName(Context context){		SharedPreferences sp = context.getSharedPreferences(AppUtil.getPackageName(context) + DB_SHARED_PREFERENCES, Context.MODE_WORLD_WRITEABLE);		Editor editor = sp.edit();		editor.putString(VERSION, AppUtil.getVersionName(context));		editor.commit();	}		public static String getDBVersion(Context context){		SharedPreferences sp = context.getSharedPreferences(AppUtil.getPackageName(context) + DB_SHARED_PREFERENCES, Context.MODE_WORLD_WRITEABLE);		return sp.getString(VERSION, "");	}	}