package com.dns.taxchina.service.model;import java.util.List;import netlib.model.BaseModel;/** * @author henzil * @version create time:2014-4-24_下午2:44:42 * @Description 微课程 解析时使用这个model */public class MicroCourseModel extends BaseModel{		private static final long serialVersionUID = 1948392833519379194L;	/**	 *  "hasNext": "",  //是否有下一页yes有，no没有    	"nowPage": "", //当前页 	 */		private boolean hasNext;		private int nowPage;		private List<BaseItemModel> dataList;	public boolean isHasNext() {		return hasNext;	}	public void setHasNext(boolean hasNext) {		this.hasNext = hasNext;	}	public int getNowPage() {		return nowPage;	}	public void setNowPage(int nowPage) {		this.nowPage = nowPage;	}	public List<BaseItemModel> getDataList() {		return dataList;	}	public void setDataList(List<BaseItemModel> dataList) {		this.dataList = dataList;	}	}