package com.dns.taxchina.ui;import android.content.Intent;import android.os.Bundle;import com.dns.taxchina_pad.R;/** * @author henzil * @version create time:2014-4-24_上午10:53:55 * @Description TODO */public abstract class BaseActivity extends BaseFragmentActivity {	@Override	protected void onCreate(Bundle arg0) {		super.onCreate(arg0);		initData();		initViews();		initWidgetActions();	}		@Override	public void startActivity(Intent intent) {		super.startActivity(intent);		overridePendingTransition(R.anim.push_right_in, R.anim.no_anim);	}		@Override	public void onBackPressed() {		super.onBackPressed();		overridePendingTransition(R.anim.no_anim, R.anim.push_right_out);	}		@Override	public void finish() {		super.finish();		overridePendingTransition(R.anim.no_anim, R.anim.push_right_out);	}}