package com.dns.taxchina.ui;import android.webkit.WebView;import com.dns.taxchina.R;import com.dns.taxchina.ui.widget.WebViewPlug;import com.dns.taxchina.ui.widget.WebViewPlug.LoadWebViewListener;/** * @author henzil * @version create time:2014-4-24_下午5:31:39 * @Description 详情页 */public class DetailActivity extends BaseActivity{	private WebView webView;		protected WebViewPlug plug;		@Override	protected void initData() {		plug = new WebViewPlug(this);			}	@Override	protected void initViews() {		setContentView(R.layout.activity_detail);		webView = (WebView) findViewById(R.id.web_view);		plug.webViewPlug("http://www.baidu.com", webView);	}	@Override	protected void initWidgetActions() {		plug.setLoadWebViewListener(new LoadWebViewListener() {			@Override			public void onPageFinished() {			}			@Override			public void onPageError() {							}		});			}}