package com.dns.taxchina.ui.adapter;import java.util.ArrayList;import java.util.List;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import com.dns.taxchina.service.model.IndexContentItemModel;import com.dns.taxchina.ui.view.IndexListView;/** * @author henzil * @version create time:2014-5-4_下午3:09:23 * @Description 首页内容listview adapter */public class IndexContentAdapter extends BaseAdapter {	private Context context;	private LayoutInflater inflater;	private String TAG;	private List<IndexContentItemModel> list = new ArrayList<IndexContentItemModel>();	public IndexContentAdapter(Context context, String TAG) {		this.context = context;		this.inflater = LayoutInflater.from(context);		this.TAG = TAG;	}	@Override	public int getCount() {		return list.size();	}	@Override	public Object getItem(int position) {		return list.get(position);	}	@Override	public long getItemId(int position) {		return position;	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		if (convertView == null) {			convertView = new IndexListView(context);		}		return convertView;	}	public void refresh(List<IndexContentItemModel> list) {		this.list.clear();		this.list.addAll(list);		notifyDataSetChanged();	}}