package com.dns.taxchina.ui.adapter;import java.util.ArrayList;import java.util.List;import android.content.Context;import android.util.Log;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.LinearLayout;import com.dns.taxchina.service.model.IndexContentItemModel;import com.dns.taxchina.ui.view.IndexListElement;import com.dns.taxchina.ui.view.IndexListViewLayout;/** * @author henzil * @version create time:2014-5-4_下午3:09:23 * @Description 首页内容listview adapter */public class IndexContentAdapter extends BaseAdapter {	private Context context;	private String TAG;	private List<IndexContentItemModel> list = new ArrayList<IndexContentItemModel>();	public IndexContentAdapter(Context context, String TAG) {		this.context = context;		this.TAG = TAG;	}	@Override	public int getCount() {		return list.size();	}	@Override	public Object getItem(int position) {		return list.get(position);	}	@Override	public long getItemId(int position) {		return position;	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		IndexListElement indexListElement = null;		if (convertView == null) {			indexListElement = new IndexListViewLayout(context);			convertView = (LinearLayout) indexListElement;			convertView.setTag(indexListElement);		} else {			indexListElement = (IndexListElement) convertView.getTag();		}		Log.e("tag", "convertView = " + convertView);		indexListElement.updateView(list.get(position), TAG);		return convertView;	}	public void refresh(List<IndexContentItemModel> list) {		this.list.clear();		this.list.addAll(list);		notifyDataSetChanged();	}}