package com.dns.taxchina.ui.adapter;import java.util.ArrayList;import java.util.List;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentStatePagerAdapter;import com.dns.taxchina.service.model.BaseItemModel;import com.dns.taxchina.service.model.NewsModel;import com.dns.taxchina.ui.fragment.NewsItemFragment;/** * @author henzil.jack E-mail:henzil.jack@gmail.com * @version 创建时间：2013-7-2 下午3:14:58 * @Description 用于做新闻样式的适配器。 */public class NewsPagerAdapter extends FragmentStatePagerAdapter {	private NewsModel newsModel;	private List<BaseItemModel> category = new ArrayList<BaseItemModel>();	public NewsPagerAdapter(FragmentManager fm, NewsModel newsModel, List<BaseItemModel> category) {		super(fm);		this.newsModel = newsModel;		if (newsModel.getCategory() != null) {			category.addAll(newsModel.getCategory());		}	}	@Override	public Fragment getItem(int position) {		if (position == 0) {			return NewsItemFragment.newInstance(newsModel, category.get(position).getId(), category.get(position).getTitle(), 0);		} else {			return NewsItemFragment.newInstance(null, category.get(position).getId(), category.get(position).getTitle(), position);		}	}	@Override	public int getCount() {		return category.size();	}	public void setNewsModel(NewsModel newsModel) {		this.newsModel = newsModel;	}	public void setCategory(List<BaseItemModel> category) {		this.category.clear();		this.category.addAll(category);		notifyDataSetChanged();	}}