package com.dns.taxchina.ui.fragment;import android.content.Intent;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.TextView;import com.dns.taxchina.R;import com.dns.taxchina.ui.CenterDetailActivity;import com.dns.taxchina.ui.CourseCollectionActivity;import com.dns.taxchina.ui.StudyRecordActivity;/** * @author henzil * @version create time:2014-4-24_下午1:59:19 * @Description 学员中心 fragment */public class CenterFragment extends BaseFragment {	private TextView title, back;	private Button studyRecord, collection, activationBtn, suggestionBtn, aboutBtn, contanctBtn;	@Override	protected void initData() {	}	@Override	protected View initViews(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {		View view = inflater.inflate(R.layout.center_fragment, container, false);		title = (TextView) view.findViewById(R.id.title_text);		title.setText("学员中心");		back = (TextView) view.findViewById(R.id.back_text);		back.setVisibility(View.GONE);		studyRecord = (Button) view.findViewById(R.id.study_record_btn);		collection = (Button) view.findViewById(R.id.course_collection_btn);		activationBtn = (Button) view.findViewById(R.id.activation_btn);		suggestionBtn = (Button) view.findViewById(R.id.suggestion_btn);		aboutBtn = (Button) view.findViewById(R.id.about_us_btn);		contanctBtn = (Button) view.findViewById(R.id.contact_us_btn);		return view;	}	@Override	protected void initWidgetActions() {		studyRecord.setOnClickListener(new View.OnClickListener() {			@Override			public void onClick(View v) {				Intent intent = new Intent(context, StudyRecordActivity.class);				startActivity(intent);			}		});		collection.setOnClickListener(new View.OnClickListener() {			@Override			public void onClick(View v) {				Intent intent = new Intent(context, CourseCollectionActivity.class);				startActivity(intent);			}		});		activationBtn.setOnClickListener(new View.OnClickListener() {			@Override			public void onClick(View v) {				Intent intent = new Intent(context, CenterDetailActivity.class);				intent.putExtra(CenterDetailActivity.DETAIL_TYPE, CenterDetailActivity.ACTIVATION_TYPE);				startActivity(intent);			}		});		suggestionBtn.setOnClickListener(new View.OnClickListener() {			@Override			public void onClick(View v) {				Intent intent = new Intent(context, CenterDetailActivity.class);				intent.putExtra(CenterDetailActivity.DETAIL_TYPE, CenterDetailActivity.SUGGESTION_TYPE);				startActivity(intent);			}		});		aboutBtn.setOnClickListener(new View.OnClickListener() {			@Override			public void onClick(View v) {				Intent intent = new Intent(context, CenterDetailActivity.class);				intent.putExtra(CenterDetailActivity.DETAIL_TYPE, CenterDetailActivity.ABOUT_US_TYPE);				startActivity(intent);			}		});		contanctBtn.setOnClickListener(new View.OnClickListener() {			@Override			public void onClick(View v) {				Intent intent = new Intent(context, CenterDetailActivity.class);				intent.putExtra(CenterDetailActivity.DETAIL_TYPE, CenterDetailActivity.CONTANCT_US_TYPE);				startActivity(intent);			}		});	}}