package com.dns.taxchina.ui.util;import com.dns.taxchina.ui.fragment.BaseFragment;import com.dns.taxchina.ui.fragment.CenterFragment;import com.dns.taxchina.ui.fragment.FindFragment;import com.dns.taxchina.ui.fragment.IndexFragment;import com.dns.taxchina.ui.fragment.MicroCourseFragment;import com.dns.taxchina.ui.fragment.NewsFragment;/** * @author henzil * @version create time:2014-4-24_上午10:59:28 * @Description 根据传递的id，返回正确的fragment */public class FragmentUtil {	public BaseFragment fragmentByIndex(int index){		BaseFragment fragment = null;		switch (index) {		case 0:			fragment = new IndexFragment();			break;		case 1:			fragment = new MicroCourseFragment();			break;		case 2:			fragment = new NewsFragment();			break;		case 3:			fragment = new FindFragment();			break;		case 4:			fragment = new CenterFragment();			break;		default:			break;		}						return fragment;	}	}