package netlib.model;import java.io.Serializable;/** * @author henzil * @E-mail lizhen@dns.com.cn * @version create time:2013-7-22_下午1:59:20 * @Description 所有model 基类。 */public abstract class BaseModel implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 5268625605268545266L;	private int code;		private int result;	private String message;	private String error;	public String getError() {		return error;	}	public void setError(String error) {		this.error = error;	}	public static long getSerialversionuid() {		return serialVersionUID;	}	public int getCode() {		return code;	}	public void setCode(int code) {		this.code = code;	}	public String getMessage() {		return message;	}	public void setMessage(String message) {		this.message = message;	}	public int getResult() {		return result;	}	public void setResult(int result) {		this.result = result;	}}