package netlib.util;import java.text.SimpleDateFormat;import java.util.Date;/** * @author henzil * @version create time:2013-9-8_下午2:56:50 * @Description 时间 util */public class DataUtil {	 /**	  * 获取现在年月年月	  *	  * @return 返回短时间字符串格式MM-dd	  */	 public static String getStringMMDDDateShort() {	  Date currentTime = new Date();	  SimpleDateFormat formatter = new SimpleDateFormat("MM/dd");	  String dateString = formatter.format(currentTime);	  return dateString;	 }}