package netlib.util;import android.content.Context;import android.content.SharedPreferences;/** * @author henzil * @version create time:2013-8-21_下午12:42:21 * @Description 设置 工具类 */public class SettingUtil {	public static final String PREFERENCES_NAME = "setting_name";		public static final String LOGIN_SINA = "login_sina";	public static final String LOGIN_QQ = "login_qq";	public static final String LOGIN_WEIXIN = "login_weixin";	public static final String LOGIN_RENREN = "login_renren";	public static final String LOGIN_DOUBAN = "login_douban";		public static final String NEW_MESSAGE = "new_message";	public static final String WIFI_IMAGE = "wifi_image";	public static final String SIGNATURE = "signature";		/**	 * 设置是否接受新消息	 * 	 * */	public static void setNewMessage(Context context, boolean newMessage) {		SharedPreferences sharedPreferences = context.getSharedPreferences(AppUtil.getPackageName(context)				+ PREFERENCES_NAME, Context.MODE_PRIVATE);		sharedPreferences.edit().putBoolean(NEW_MESSAGE, newMessage).commit();	}	/**	 * 获取是否接受新消息	 * 	 * */	public static boolean getNewMessage(Context context) {		SharedPreferences sharedPreferences = context.getSharedPreferences(AppUtil.getPackageName(context)				+ PREFERENCES_NAME, Context.MODE_PRIVATE);		return sharedPreferences.getBoolean(NEW_MESSAGE, true);	}	/**	 * 设置是否在wifi下加载图片	 * 	 * */	public static void setWifiImage(Context context, boolean wifiImage) {		SharedPreferences sharedPreferences = context.getSharedPreferences(AppUtil.getPackageName(context)				+ PREFERENCES_NAME, Context.MODE_PRIVATE);		sharedPreferences.edit().putBoolean(WIFI_IMAGE, wifiImage).commit();	}	/**	 * 获取是否在wifi下获取图片	 * 	 * */	public static boolean getWifiImage(Context context) {		SharedPreferences sharedPreferences = context.getSharedPreferences(AppUtil.getPackageName(context)				+ PREFERENCES_NAME, Context.MODE_PRIVATE);		return sharedPreferences.getBoolean(WIFI_IMAGE, false);	}	/**	 * 设置新浪登陆状态	 * 	 * */	public static void setSharaLoginSina(Context context, boolean isLogin) {		SharedPreferences sharedPreferences = context.getSharedPreferences(AppUtil.getPackageName(context)				+ PREFERENCES_NAME, Context.MODE_PRIVATE);		sharedPreferences.edit().putBoolean(LOGIN_SINA, isLogin).commit();	}	/**	 * 设置QQ登陆状态	 * 	 * */	public static void setSharaLoginQQ(Context context, boolean isLogin) {		SharedPreferences sharedPreferences = context.getSharedPreferences(AppUtil.getPackageName(context)				+ PREFERENCES_NAME, Context.MODE_PRIVATE);		sharedPreferences.edit().putBoolean(LOGIN_QQ, isLogin).commit();	}	/**	 * 设置微信登陆状态	 * 	 * */	public static void setSharaLoginWeiXin(Context context, boolean isLogin) {		SharedPreferences sharedPreferences = context.getSharedPreferences(AppUtil.getPackageName(context)				+ PREFERENCES_NAME, Context.MODE_PRIVATE);		sharedPreferences.edit().putBoolean(LOGIN_WEIXIN, isLogin).commit();	}	/**	 * 设置人人登陆状态	 * 	 * */	public static void setSharaLoginRenRen(Context context, boolean isLogin) {		SharedPreferences sharedPreferences = context.getSharedPreferences(AppUtil.getPackageName(context)				+ PREFERENCES_NAME, Context.MODE_PRIVATE);		sharedPreferences.edit().putBoolean(LOGIN_RENREN, isLogin).commit();	}	/**	 * 设置豆瓣登陆状态	 * 	 * */	public static void setSharaLoginDouBan(Context context, boolean isLogin) {		SharedPreferences sharedPreferences = context.getSharedPreferences(AppUtil.getPackageName(context)				+ PREFERENCES_NAME, Context.MODE_PRIVATE);		sharedPreferences.edit().putBoolean(LOGIN_DOUBAN, isLogin).commit();	}}