package netlib.util;import android.content.Context;import android.content.SharedPreferences;/** * @author henzil * @version create time:2014-5-15_上午9:38:48 * @Description 设置 util */public class SettingUtil {	public static final String PREFERENCES_NAME = "setting_name";	public static final String WIFI_DOWNLOAD = "wifi_download";	/**	 * 设置是否在wifi下加载图片	 * 	 * */	public static void setWifiDoSomeThing(Context context, boolean wifiImage) {		SharedPreferences sharedPreferences = context.getSharedPreferences(AppUtil.getPackageName(context)				+ PREFERENCES_NAME, Context.MODE_PRIVATE);		sharedPreferences.edit().putBoolean(WIFI_DOWNLOAD, wifiImage).commit();	}	/**	 * 获取是否在wifi下获取图片	 * 	 * */	public static boolean getWifiDoSomeThing(Context context) {		SharedPreferences sharedPreferences = context.getSharedPreferences(AppUtil.getPackageName(context)				+ PREFERENCES_NAME, Context.MODE_PRIVATE);		return sharedPreferences.getBoolean(WIFI_DOWNLOAD, false);	}}